mapper.insert:
				INSERT INTO schedule (
						 SCHEDULE_ID
            		   , EVENT_DATE 
            		   , EVENT_START_TIME 
            		   , EVENT_END_TIME 
            		   , PATIENT_NAME
            		   , TREAT_DVS_CODE 
            		   , SIMPLE_MSG_CTNT
            		   , EVENT_STATUS
            		   , EXAM_USR_NM
            		   , PRESCRIBER_USR_NM
            		   , DELETE_YN
            		   , CREATE_DT
            		   , CREATE_USR_ID
                   )
            VALUES (#{scheduleId}
                    , #{eventDate}
                    , #{eventStartTime}
                    , #{eventEndTime}
                    , #{patientName}
                    , #{treatDvsCode}
                    , #{simpleMsgCtnt}
                    , #{eventStatus}
                    , #{examUsrNm}
                    , #{prescriberUsrNm}
                    , IFNULL(#{deleteYn}, 'N')
                    , now()
                    , #{createUsrId}
                   )

mapper.update:
	   UPDATE schedule
                 SET 
                 EVENT_DATE = #{eventDate}
                 , EVENT_START_TIME = #{eventStartTime}
                 , EVENT_END_TIME = #{eventEndTime}
                 , PATIENT_NAME = #{patientName}
                 , TREAT_DVS_CODE = #{treatDvsCode}
                 , SIMPLE_MSG_CTNT = #{simpleMsgCtnt}
                 , EVENT_STATUS = #{eventStatus}
                 , EXAM_USR_NM = #{examUsrNm}
                 , PRESCRIBER_USR_NM = #{prescriberUsrNm}
                 , DELETE_YN = IFNULL(#{deleteYn}, 'N')
                 , UPDATE_DT = now()
                 , UPDATE_USR_ID = #{updateUsrId}
           WHERE SCHEDULE_ID = #{scheduleId}

mapper.delete:
	   UPDATE schedule
          SET DELETE_YN = IFNULL(#{deleteYn}, 'N')	
        WHERE SCHEDULE_ID = #{scheduleId}
